    static int D = 0;
    
    public static void main(String[] args) {
        for (int a=0;a<=2998;a++) {
            for (int b=a+1;b<=2999;b++) {
                
                int sum = pentagonal[a] + pentagonal[b];
                int dif = Math.abs(pentagonal[a] - pentagonal[b]);
                
                if ((checkIfPentagonal(sum)) && (checkIfPentagonal(dif))) {
                    System.out.println("a="+pentagonal[a]+". b="+pentagonal[b]);
                    
                    D = (dif < D) ? dif : D;
                }
            }
        }
        System.out.println(D);
    }
    
    protected static boolean checkIfPentagonal(int p) {
        return ( (((Math.sqrt((24*p)+1)) )+1) % 6 == 0);
    }
    
    protected static void genPen(int l) {
        System.out.print("{");
        for (int c=1;c<=l;c++) {
            System.out.print(pN(c)+", ");
            if (c%100==0) {
                System.out.println("");
            }
        }
        System.out.print("}");
    }
    
    protected static int pN(int n) {
        return ((n*((3*n)-1))/2);
    }
